import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import Todo from './Todo';
import Appform from '../Appform';
import GetCompTasks from '../BackEndComponents/GetCompTasks';

function TodoList(props) {
  const [todos, setTodos] = useState([]);

  const addTodo = todo => {
    if (!todo.text || /^\s*$/.test(todo.text)) {
      return;
    }

    const newTodos = [todo, ...todos];

    setTodos(newTodos);
    console.log(...todos);
    ReactDOM.render(
      <React.StrictMode>
        <Appform />
      </React.StrictMode>,
      document.getElementById('root')
    );
  };

  const updateTodo = (todoId, newValue) => {
    if (!newValue.text || /^\s*$/.test(newValue.text)) {
      return;
    }

    setTodos(prev => prev.map(item => (item.id === todoId ? newValue : item)));
  };

  const buttonn = () => {
    if (props.showb) {
      return <button className="todo-button" onClick={() => addTodo({ text: 'New Todo' })}>Add Todo</button> ;
    } else {
      return ;
    }
  }

  if (props.showb) {
    <button className="todo-button" onClick={() => addTodo({ text: 'New Todo' })}>Add Todo</button>
  }

  const removeTodo = id => {
    const removedArr = [...todos].filter(todo => todo.id !== id);

    setTodos(removedArr);
  };

  const completeTodo = id => {
    let updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        todo.isComplete = !todo.isComplete;
      }
      return todo;
    });
    setTodos(updatedTodos);
  };
  return (
    <>
      <h1>{props.name}</h1>
      <div className='buttonlist'>
      <button className="todo-button" onClick={() => addTodo({ text: 'New Todo' })}>Add Todo</button>
      </div>
      
      <Todo
        todos={todos}
        completeTodo={completeTodo}
        removeTodo={removeTodo}
        updateTodo={updateTodo}
      />

      <GetCompTasks />
    </>
  );
}

export default TodoList;
